mapper.select:
select count(*)
from tb_attendance ta
    where ta.ATTENDANCE_DATE = #{DATE}
    and ta.ATTENDANCE_ROOM = #{searchRoom}
    and ta.ATTENDANCE_CHANNEL = #{searchChannel}

select tmu.MU_CD, tmu.MU_NAME, tmu.MU_PHONE, tS.SCHOOL_NAME, tr.REGIST_MU_GRADE, DATE_FORMAT(tr.REGIST_FIRST_DATE, '%Y-%m-%d') as REGIST_FIRST_DATE from tb_member_user tmu left outer join tb_regist tr on tmu.MU_CD = tr.REGIST_MU_CD and tr.DLT_YN != 'Y' and #{DATE} between tr.REGIST_START_DATE and tr.REGIST_END_DATE left outer join tb_school ts on tr.REGIST_MU_SCHOOL = ts.SCHOOL_CODE and ts.DLT_YN != 'Y' inner join tb_attendance ta on tr.REGIST_MU_CD = ta.ATTENDANCE_MU_CD and ta.ATTENDANCE_DATE = #{DATE} where tmu.DLT_YN != 'Y' and tmu.MB_TYPE != 'M'
and tr.REGIST_CHANNEL = #{searchChannel}
and tr.REGIST_ROOM = #{searchRoom}
order by tr.REGIST_SEAT asc LIMIT #{startIndex, jdbcType=INTEGER}, #{rowsPerPage, jdbcType=INTEGER};

select ta.*, ts.SEAT_NAME, tmu.MU_NAME, tmu.MU_PHONE, tmu.MU_PHONE, tr.REGIST_MU_GRADE, tr.REGIST_ROOM, ts2.SCHOOL_NAME, DATE_FORMAT(tr.REGIST_FIRST_DATE, '%Y-%m-%d') as REGIST_FIRST_DATE from tb_attendance ta left outer join tb_seat ts on ta.ATTENDANCE_SEAT = ts.SEAT_SEQ and ts.DLT_YN != 'Y' left outer join tb_member_user tmu on ta.ATTENDANCE_MU_CD = tmu.MU_CD and tmu.DLT_YN != 'Y' left outer join tb_regist tr on tr.REGIST_MU_CD = ta.ATTENDANCE_MU_CD and tr.DLT_YN != 'Y' and tr.REGIST_CHANNEL = #{searchChannel} and tr.REGIST_ROOM = #{searchRoom} left outer join tb_school ts2 on ts2.SCHOOL_CODE = tr.REGIST_MU_SCHOOL where ta.ATTENDANCE_DATE = #{DATE} and ta.ATTENDANCE_CHANNEL = #{searchChannel} and ta.ATTENDANCE_ROOM = #{searchRoom}
and tmu.MU_NAME like (CONCAT('%', #{searchText}, '%'))
order by ts.SEAT_SEQ asc LIMIT #{startIndex, jdbcType=INTEGER}, #{rowsPerPage, jdbcType=INTEGER};

select *
from tb_attendance
where ATTENDANCE_DATE = #{DATE}
  and ATTENDANCE_CHANNEL = #{CHANNEL_CODE}
  and ATTENDANCE_MU_CD = #{MU_CD}
  and ATTENDANCE_ROOM = #{ROOM_CODE}

select *
from tb_attendance
where ATTENDANCE_MU_CD = #{MU_CD}
and ATTENDANCE_DATE = #{DATE}

mapper.update:
update tb_attendance
set ATTENDANCE_SEAT = #{REGIST_SEAT}
where REGIST_SEQ = #{REGIST_SEQ}
    and ATTENDANCE_DATE >= #{DATE}
